import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import Card from "../UI/Card";

const Expenses = (props) => {
  const [expenseYear, setExpenseYear] = useState("2020");

  const selectExpenseYearHandler = (selectedExpenseYear) => {
    setExpenseYear(selectedExpenseYear);
  };

  console.log(new Date(2021, 5, 12).getFullYear().toString());
  console.log(expenseYear);
  console.log(new Date(2021, 5, 12).getFullYear().toString() === expenseYear);

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={expenseYear}
          onExpenseFilter={selectExpenseYearHandler}
        />
        {props.expenses
          .filter(
            (expense) =>
              expense.date.getFullYear().toString() === setExpenseYear
          )
          .map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))}
      </Card>
    </div>
  );
};

export default Expenses;
